--  DDL for Function FN_GET_CNT_RPTP
--------------------------------------------------------

  CREATE OR REPLACE FUNCTION "TEAM4_202209F"."FN_GET_CNT_RPTP" (P_DEPT_NO IN VARCHAR2, P_DATE_INFO IN VARCHAR2)
    /*
    1) 부서번호, 찾고 싶은 일자 (일별,주별,월별) => 확인보고 수
    */
  RETURN NUMBER
IS
    V_CNT_RPTP NUMBER;
BEGIN

    IF P_DATE_INFO = '일별' THEN
        SELECT COUNT(RPTP_NO) INTO V_CNT_RPTP
          FROM RPTP
         WHERE RPTP_CMPTN_YN = 'Y'
           AND TO_CHAR(RPTP_WRT_YMD,'YYYYMMDDHH24MISS') BETWEEN TO_CHAR(SYSDATE,'YYYYMMDD')||'000000' AND TO_CHAR(SYSDATE,'YYYYMMDD')||'235959';   
    ELSIF P_DATE_INFO = '주별' THEN
        SELECT COUNT(RPTP_NO) INTO V_CNT_RPTP
          FROM RPTP
         WHERE RPTP_CMPTN_YN = 'Y'
           AND TO_CHAR(RPTP_WRT_YMD,'YYYYMMDDHH24MISS') BETWEEN TO_CHAR(SYSDATE-7,'YYYYMMDD')||'000000' AND TO_CHAR(SYSDATE,'YYYYMMDD')||'235959';   
    ELSIF P_DATE_INFO = '월별' THEN
        SELECT COUNT(RPTP_NO) INTO V_CNT_RPTP
          FROM RPTP
         WHERE RPTP_CMPTN_YN = 'Y'
           AND TO_CHAR(RPTP_WRT_YMD,'YYYYMMDDHH24MISS') BETWEEN TO_CHAR(TRUNC(SYSDATE, 'MONTH'), 'YYYYMMDD')||'000000' AND TO_CHAR(LAST_DAY(SYSDATE), 'YYYYMMDD')||'235959';           
    END IF;

    RETURN V_CNT_RPTP;
END;

