WITH T AS(
    SELECT 
        A.EML_NO,
        FN_GET_EMP_NM(A.EMP_NO) AS EMP_NM,
        (SELECT B.EML_TAG_COLOR FROM EML_TAG B WHERE B.EML_TAG_NO = A.EML_TAG_NO) AS EML_TAG_COL,
        A.EML_CLSF_NO,
        A.EML_TTL,
        A.EML_CN,
        A.EML_TRSM_DT,
        LAG(A.EML_TRSM_DT) OVER(ORDER BY A.EML_TRSM_DT DESC) AS PREV,
        A.EML_IMPOR_YN,
        A.EML_SNDR_NO,
        A.FILE_GROUP_NO,
        C.EML_RCPTN_YN,
        CASE WHEN C.EML_RCPTN_YN='Y' THEN '수신' ELSE '미수신' END AS RCVR_YN,
        NVL(FN_GET_EMP_NM_FROM_EML(C.EML_RCVR_ADDR), '외부') AS RCVR
    FROM EML A
    JOIN EMAIL_RCVR C ON A.EML_NO=C.EML_NO
    WHERE A.EML_CLSF_NO='EML_CLSF01' AND A.EMP_NO='202301'
    ORDER BY A.EML_TRSM_DT DESC
)
SELECT 
    T.EML_NO, 
    T.EMP_NM, 
    T.EML_TAG_COL, 
    T.EML_CLSF_NO, 
    T.EML_TTL, 
    T.EML_CN, 
    T.EML_TRSM_DT, 
    T.PREV,
    CASE WHEN TO_CHAR(T.EML_TRSM_DT,'YYYY-MM-DD') != TO_CHAR(NVL(T.PREV,'1234-12-12'),'YYYY-MM-DD') THEN 1
        ELSE 0
    END RSLT,
    T.EML_IMPOR_YN,
    T.EML_SNDR_NO,
    T.FILE_GROUP_NO,
    T.RCVR_YN,
    T.RCVR
