    1) 직원번호 -> 직원의 부서의 이번 주 미확인 일일업무보고 수
    */
  RETURN VARCHAR2
IS
    V_DEPT_NO DEPT.DEPT_NO%TYPE;
    V_RPTP_CNT NUMBER;
BEGIN
    SELECT DEPT_NO INTO V_DEPT_NO
     FROM EMP
    WHERE EMP_NO =P_EMP_NO;

    SELECT COUNT(RPTP_NO) INTO V_RPTP_CNT
    FROM RPTP
    WHERE EMP_NO IN (SELECT EMP_NO FROM EMP WHERE DEPT_NO = V_DEPT_NO)
      AND RPTP_WRT_YMD >= TRUNC(SYSDATE, 'IW')
      AND RPTP_CMPTN_YN = 'N'
    GROUP BY TO_CHAR(RPTP_WRT_YMD, 'YYYY-IW');
    RETURN V_RPTP_CNT;
END;

